function err = fit_function_pipeline_general(inputs,wavenumber,data,e_REFL)% Variables in usekB = 1.38064852E-23; % Boltzmann constant (J/K)h = 6.626070040E-34; % Planck constant (Js)c =  299792458; % Speed of light in vacuum (m/s)% Functionfit_fun = (e_REFL*inputs(1)) .* ((2.e+8*h*c*c*wavenumber.^3) ./ (exp(100*h*wavenumber*c./(kB*inputs(2)))-1));err = immse(fit_fun,data);end